function getUserSession() {
  let user = null;
  $.ajax({
    async: false,
    url: "controllers/newController.php",
    method: "POST",
    data: { getUserSession: 1 },
    dataType: "json",
    success: function (data) {
      user = data.firstName + " " + data.middleName + " " + data.lastName;
    },
  });
  return user;
}
